CREATE TABLE ADDRESSES (ID INTEGER GENERATED BY DEFAULT AS IDENTITY, CITY VARCHAR(255) NOT NULL, COUNTRY VARCHAR(255) NOT NULL, POSTCODE INTEGER NOT NULL, STREET VARCHAR(255) NOT NULL, CLIENT_ID INTEGER NOT NULL, PRIMARY KEY (ID));
CREATE TABLE CLIENTS (ID INTEGER GENERATED BY DEFAULT AS IDENTITY, FIRST_NAME VARCHAR(255) NOT NULL, LAST_NAME VARCHAR(255) NOT NULL, TELEPHONE VARCHAR(255) NOT NULL, PRIMARY KEY (ID));
CREATE TABLE PURCHASES (CODE VARCHAR(255) NOT NULL, CREATED_ON TIMESTAMP(6) WITH TIME ZONE, ORDER_TOTAL FLOAT(53) NOT NULL, PILOTES INTEGER NOT NULL, ADDRESS_ID INTEGER NOT NULL, PRIMARY KEY (CODE));
ALTER TABLE IF EXISTS ADDRESSES ADD CONSTRAINT CONSTRAINT_ADDRESSES_CLIENTS FOREIGN KEY (CLIENT_ID) REFERENCES CLIENTS;
ALTER TABLE IF EXISTS PURCHASES ADD CONSTRAINT CONSTRAINT_PURCHASES_ADDRESSES FOREIGN KEY (ADDRESS_ID) REFERENCES ADDRESSES;
CREATE SEQUENCE order_sequence START WITH 13 INCREMENT BY 1 MINVALUE 1;